class Solution {
public:
    TreeNode* bstFromPreorder(vector<int>& preorder) {
        if(preorder.size()==0)
            return NULL;
        TreeNode* root=new TreeNode(preorder[0]);
        if(preorder.size()==1)
            return root;
        vector<int>left;
        vector<int>right;
        for(int i=0;i<preorder.size();i++)
