            if(preorder[i]>preorder[0])
                right.push_back(preorder[i]);
            else if(preorder[i]<preorder[0])
                left.push_back(preorder[i]);
        }
        root->left=bstFromPreorder(left);
        root->right=bstFromPreorder(right);
        return root;
    }
};
