/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    //Function to insert in the BST
    void insert(TreeNode * &root , int key){
        if(root == nullptr){
           root = new TreeNode(key);
           return ;
        }
        //if key is greater than root then insert to the right
        if(key > root->val){
           insert(root->right,key);
        }
        else{ //if key is smaller than root then insert to the left
            insert(root->left,key);
        }

    }

    TreeNode* bstFromPreorder(vector<int>& preorder) {
        TreeNode* root ;
        //Iterating over Node Values and sending to the insert function
        for(auto key : preorder){
            insert(root,key);
        }
        return root;
    }
};

