class Solution {

    TreeNode * Solve(vector<int>& preorder, int start ,int end)
    {
        if(start>end)
            return NULL;

        TreeNode * root = new TreeNode(preorder[start]);

        int i = start+1;
        for(; i<=end ; i++)
        {
            if(preorder[start]<preorder[i])
                break;
        }

        root->left = Solve(preorder, start+1 , i-1);
        root->right = Solve(preorder , i, end);

        return root;
    }

    TreeNode * SolveOptimised(vector<int>& preorder, int &index, TreeNode* p , TreeNode * q )
    {
        if(index >= preorder.size())
            return NULL;

        if((p && p->val > preorder[index]) || (q && q->val < preorder[index]) )
            return NULL;

        TreeNode * root = new TreeNode (preorder[index++]);

        root->left = SolveOptimised(preorder , index , p, root);
        root->right = SolveOptimised(preorder , index , root , q);

        return root;
    }

public:
    TreeNode* bstFromPreorder(vector<int>& preorder) {

        // Methos 1 , TC :O(n square)
        return Solve(preorder, 0 , preorder.size()-1);

        // Methos 2 , TC :O(n)

        int index = 0;
        return SolveOptimised(preorder , index , NULL, NULL);

    }
};
