class Solution {
public:
	TreeNode* bstFromPreorder(vector<int>& p) {
		TreeNode* root = new TreeNode(p[0]), *cur = root;
        stack<TreeNode*> s({root}); //push the node with no children
		for(int i = 1; i < p.size(); i++)
		{
			if(p[i] < s.top()->val)
			{
				cur->left = new TreeNode(p[i]);
				cur = cur->left;
				s.push(cur);
			}
            else
            {   //check the previous nodes for the best pos to insert
