/**
 * *Time: O(N)
 * *Space: O(N)
 */
// Runtime: 60 ms, faster than 99.53% of JavaScript online submissions for Construct Binary Search Tree from Preorder Traversal.
// Memory Usage: 43.5 MB, less than 23.36% of JavaScript online submissions for Construct Binary Search Tree from Preorder Traversal.
const bstFromPreorder = preorder => {
	if (!preorder.length) return null;

	let idx = 0;
	const traverse = (L, R) => {
		// exhasusted all elements
		if (idx === preorder.length) return null;

		// invalid BST
		if (preorder[idx] < L) return null;
		if (preorder[idx] > R) return null;

		// create node
		const root = new TreeNode(preorder[idx++]);

		// add elements to Left then Right
		root.left = traverse(L, root.val);
		root.right = traverse(root.val, R);

		return root;
	};

	// set Left and Right boundary
	return traverse(-Infinity, Infinity);
};
```

