```
TreeNode* build(TreeNode* root,int val){
        if(root==NULL)
            root=new TreeNode(val);
        else if(val<=root->val)
            root->left=build(root->left,val);
        else if(val>root->val)
            root->right=build(root->right,val);
        return root;
    }
    TreeNode* bstFromPreorder(vector<int>& preorder) {
