        if(curr>=vec.size() || vec[curr]<min || vec[curr]>max) return NULL;
        int ele=vec[curr++];
        TreeNode *newnode=new TreeNode(ele);
        newnode->left=solve(vec,min,ele-1,curr);
        newnode->right=solve(vec,ele+1,max,curr);
