```java
class Solution {
    public TreeNode bstFromPreorder(int[] preorder) {
        return build(preorder, 0, preorder.length - 1);
    }

    private TreeNode build(int[] preorder, int i, int j) {
        if (i > j) {
            return null;
        }

        TreeNode node = new TreeNode(preorder[i]);
        int firstLarger =  firstLarger(preorder, i + 1, preorder.length - 1, preorder[i]);

        node.left = build(preorder, i + 1, firstLarger - 1);
        node.right = build(preorder, firstLarger, j);
        return node;
    }

    private int firstLarger(int[] preorder, int start, int end, int target) {
        int i = start; // [start ... i-1] contains x < target
        int j = end; // [j+1 ... end] contains x > target

        while (i <= j) {
            int mid = i + (j - i) / 2;
            if (preorder[mid] > target) {
                j = mid - 1;
            } else {
                i = mid + 1;
            }
        }
        return j + 1;
    }
}
