class Solution {
    fun bstFromPreorder(A: IntArray): TreeNode? {
        fun go(root: TreeNode?, x: Int) {
            if (x < root?.`val`!!) {
                if (root?.left != null)
                    go(root?.left, x)
                else
                    root?.left = TreeNode(x)
            } else {
                if (root?.right != null)
                    go(root?.right, x)
                else
                    root?.right = TreeNode(x)
            }
        }
        var root = TreeNode(A[0])
        for (x in A.slice(1 until A.size))
            go(root, x)
        return root
    }
}
