//O(N) Time O(Ht of tree) Space
class Solution {
public:
    TreeNode* bstFromPreorder(vector<int>& preorder) {
        //preorder is root left right, so always we get the root at first

        int i=0; // to runthrough preorder vector
        TreeNode* root = Helper(preorder,i,INT_MAX); 

        return root;
    }

    TreeNode* Helper(vector<int>& preorder, int &i, int UpperBound){
        if(i==preorder.size() || preorder[i]>UpperBound) return NULL;

        //create node 
        TreeNode* root = new TreeNode(preorder[i]);
        i++; // next ele

        //left means - less than root
        root->left = Helper(preorder,i, root->val);
         //right means - more than root - so continues same
        //the guy with less than root is tackled above itself, so it wont come
        root->right = Helper(preorder,i, UpperBound);

        return root;
    }


};
```

