        // support variables
        int md = upper_bound(begin(preorder) + st, begin(preorder) + ed, preorder[st]) - begin(preorder);
        return new TreeNode(preorder[st++], bstFromPreorder(preorder, st, md), bstFromPreorder(preorder, md, ed));
