class Solution {
public:
    TreeNode* bstFromPreorder(vector<int>& preorder) {
        return buildtree(preorder, 0, preorder.size());
    }
private:
    TreeNode* buildtree(vector<int>& preorder, int start, int end) {
        if (start >= end) {
            return nullptr;
        }

        auto middle = upper_bound(preorder.begin() + start + 1, 
                                  preorder.begin() + end, 
                                  preorder[start]) - preorder.begin();
        return new TreeNode(preorder[start], buildtree(preorder, start + 1, middle), buildtree(preorder, middle, end));
    }
};
