```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */


class Solution
{
    public:
    void dfs(TreeNode* root,vector<int>& preorder, int index)
    {
        if(root==NULL)
        {
            return;
        }
        if(preorder[index]<root->val)
        {
            if(root->left==NULL)
            {
                root->left=new TreeNode(preorder[index]);
                return;
            }
                dfs(root->left,preorder,index);
        }
        else
        {
            if(root->right==NULL)
            {
                root->right=new TreeNode(preorder[index]);
                return;
            }
            dfs(root->right,preorder,index);
        }

    }
    TreeNode* bstFromPreorder(vector<int>& preorder) 
    {
        TreeNode* root=new TreeNode(preorder[0]);
        for(int i=1;i<preorder.size();i++)
        {
            dfs(root,preorder,i);
        }
        return root;
    }
};
```

