class Solution {
public:
    int bitwiseComplement(int n) {
        unsigned int mask = 0x80000000;
        int res = 0;

        if (n == 0)
            return 1;
        for (; mask != 0; mask = mask >> 1)
            if (n & mask)
                break;
        for (; mask != 0; mask = mask >> 1) {
            if (!(n & mask))
                res += mask;
        }
        return res;
    }
};
```

