class Solution {
public:
    int bitwiseComplement(int n) {
        int complement,count=0;
        int temp=n;

        if(n==0)
        {
            return 1;
        }

        //Calculating nearest complete binary number
        //number+complement=complete binary number
        //Eg: 110 + 001 = 111 (7 in decimal)
        while(temp!=0)
        {
            count++;
            temp=temp>>1;
        }

        temp=pow(2,count)-1;

        return temp-n;
    }
};
