class Solution {
    public int numPairsDivisibleBy60(int[] time) {
        return numsDivisibleByK(time, 60);
    }

    private int numsDivisibleByK(int[] nums, int k) {
        int answer = 0;
        int[] hash = new int[k];

        for (int num : nums) {
            int current = num % k;
            int opposite = (k - current) % k;

            answer += hash[opposite];
            hash[current]++;
        }

        return answer;
    }
}
