public:
    int numPairsDivisibleBy60(vector<int>& time) 
    {
        const int N = time.size();        
        unordered_map<int, int> table;

        int numPairs = 0;
        for (int i = 0; i < N; ++i)
        {
            int r = time[i] % 60;
            numPairs += table[r > 0 ? 60-r : 0];
            table[r]++;
        }

        return numPairs;
    }
};
