    int numPairsDivisibleBy60(vector<int>& time) {
        int A[60] = {0};
        for (int t : time) A[t % 60]++;

		// A[0] and A[30] are special k * (k-1) / 2 pairs.
        int cnt = A[0] * (A[0] - 1) / 2 + A[30] * (A[30] - 1) / 2;

        #pragma unroll
        for (int i = 1; i < 30; ++i) {
            cnt += A[i] * A[60 - i];
        }

        return cnt;
    }
