class Solution {
public:
    int numPairsDivisibleBy60(vector<int>& time) {
        vector<int> rem(60); //initialize remainder array which is the size of divisor

        for(int elem : time)
            rem[elem%60]+=1; //iterate the given elements and assign their remainder into the vector

        int output = 0;
        output += ((rem[0]-1)*rem[0])/2;  //special case 1: where 0 can be a remainder which can form a pair with only itself
        output += ((rem[30]-1)*rem[30])/2; //special case 2: where 30 can be a remainder where it can form a pair with only itself
		for(int i=1;i<=29;++i){  //iterate through first half excluding 0&30(as it a special case) to avoid duplication
            output+=rem[i]*rem[60-i]; //check in the remainder vector for corresponding pair to increment the count of divisibility 
        }

        return output;

    }
};
