class Solution:
    def numPairsDivisibleBy60(self, time: List[int]) -> int:
        if not time:
            return 0
        helper = [0] * 60
        for x in time:
            helper[x % 60] += 1

        res = helper[0] * (helper[0]-1)
        res += helper[30] * (helper[30]-1)
        for i in range(1, 30):
            if helper[i]:
                res += helper[i] * helper[60-i]
        for i in range(31, 60):
            if helper[i]:
                res += helper[i] * helper[60-i]
        return res // 2
