class Solution {
public:
    using VI = vector<int>;
    using Map = unordered_map<int, int>;
    int numPairsDivisibleBy60(VI& A, Map m = {}, int pairs = 0) {
        transform(A.begin(), A.end(), A.begin(), [](auto x) { return x % 60; });
        for (auto x: A) {
            auto y = (60 - x) % 60;
            pairs += m[y];
            ++m[x];
        }
        return pairs;
    }
};
