class Solution {
    public int numPairsDivisibleBy60(int[] time) {
        Map<Integer, Integer> map = new HashMap<>();
        int ans = 0;
        for (int t : time) {
            int mod = t % 60;
            int rem = (mod == 0)? 0 : 60 - mod;
            ans += map.getOrDefault(rem, 0);
            map.put(mod, 1 + map.getOrDefault(mod, 0));
        }

        return ans;
    }
}

