class StreamChecker:
    def rollingHash(self, Hash, p_pow, c, mod = 1e9+7):
        return ((Hash + ord(c) * p_pow) % mod, (p_pow * 31) % mod)

    def __init__(self, words):
        self.suffix_hash, self.word_hash, self.query_stream = set(), set(), deque()
        for w in words:
            Hash, p_pow = 0, 1
            for c in w[::-1]:
                Hash, p_pow = self.rollingHash(Hash, p_pow, c)
                self.suffix_hash.add(Hash)
            self.word_hash.add(Hash)

    def query(self, c):
        self.query_stream.appendleft(c)
        Hash, p_pow = 0, 1
        for c in self.query_stream:
            Hash, p_pow = self.rollingHash(Hash, p_pow, c)
            if Hash not in self.suffix_hash: return False
            elif Hash in self.word_hash: return True
