		return temp->word_ends;
	}
};

class StreamChecker {
	Trie my_trie;
	string str = "";
	int w_len = 0;
public:
	StreamChecker(vector<string>& words) {
		for(auto w : words) {
			reverse(w.begin(), w.end());
			w_len = max(w_len, (int)w.length());
			my_trie.insert(w);
		}
	}

	bool query(char letter) {
		str = letter + str;

		if(str.length() > w_len)
			str.pop_back();

		return my_trie.search(str);
	}
};

