class StreamChecker {
public:
    const static int K = 26;
    int node = 0;

    struct Vertex {
        int next[K];
        bool leaf = false;
        int p = -1;
        char pch;
        int link = -1;
        int go[K];
        bool seen = false;

        Vertex(int p=-1, char ch='$') : p(p), pch(ch) {
            fill(begin(next), end(next), -1);
            fill(begin(go), end(go), -1);
        }
    };

    vector<Vertex> t;

    void add_string(string const& s) {
        int v = 0;
        for (char ch : s) {
            int c = ch - 'a';
            if (t[v].next[c] == -1) {
                t[v].next[c] = t.size();
                t.emplace_back(v, ch);
            }
            v = t[v].next[c];
        }
        t[v].leaf = true;
    }

    int get_link(int v) {
        if (t[v].link == -1) {
            if (v == 0 || t[v].p == 0)
                t[v].link = 0;
            else
                t[v].link = go(get_link(t[v].p), t[v].pch);
        }
        return t[v].link;
    }

    int go(int v, char ch) {
        int c = ch - 'a';
        if (t[v].go[c] == -1) {
            if (t[v].next[c] != -1)
                t[v].go[c] = t[v].next[c];
            else
                t[v].go[c] = v == 0 ? 0 : go(get_link(v), ch);
        }
        return t[v].go[c];
    }

    bool DFS(const int u) {
        if (t[u].seen)
            return t[u].leaf;
        t[u].seen = true;
        if (!t[u].leaf)
            t[u].leaf = DFS(get_link(u));
        for (int i = 0; i < 26; i++) {
            if (t[u].next[i] != -1)
                DFS(t[u].next[i]);
        }
        return t[u].leaf;
    }

    StreamChecker(vector<string>& words) {
        t.push_back(Vertex());
        for (auto& w : words)
            add_string(w);
        DFS(0);
    }

    bool query(char letter) {
        node = go(node, letter);
        return t[node].leaf;
    }
};
```

