public class Trie {

	private class Node {

		char data;
		Node[] children;
		boolean isTerminal;

		public Node(char data, boolean isTerminal) {
			this.data = data;
			this.isTerminal = isTerminal;
			this.children = new Node[26];
		}

	}

	private Node root;

	// O(1)
	public Trie() {
		this.root = new Node('\0', false);
	}

	// O(word.length)
	public void addWord(String word) {
		Node curr = root;
		for (int i = word.length() - 1; i >= 0; i--) {
			char ch = word.charAt(i);
			Node temp = curr.children[ch - 'a'];
			if (temp == null) {
				temp = new Node(ch, false);
				curr.children[ch - 'a'] = temp;
			}
			curr = temp;
		}
		curr.isTerminal = true;
	}

	// O(word.length)
	public boolean searchWord(StringBuilder sb) {
		Node curr = root;
		for (int i = sb.length() - 1; i >= 0; i--) {
			char ch = sb.charAt(i);
			Node temp = curr.children[ch - 'a'];
			if (temp == null)
				return false;
			curr = temp;
			if (curr.isTerminal)
				return true;
		}
		return false;
	}

}

class StreamChecker {

	Trie trie;
	StringBuilder sb;

	// O(words.length*words[i].length)
	public StreamChecker(String[] words) {
		trie = new Trie();
		sb = new StringBuilder();
		for (String word : words)
			trie.addWord(word);
	}

	// O(query.length)
	public boolean query(char letter) {
		sb.append(letter);
		return trie.searchWord(sb);
	}
}

