class StreamChecker {
public:
    class TrieNode {
    public:
        TrieNode() : children {}, leaf(false) {}
        bool leaf;
        TrieNode* children[26];
    };

    StreamChecker(std::vector<std::string>& words) {
        root = new TrieNode();

        for (auto& word : words) {
            TrieNode* it = root;        
            for (char c : word) {
                if (!it->children[c - 'a'])
                    it->children[c - 'a'] = new TrieNode();
                it = it->children[c - 'a'];
            }
            it->leaf = true;
        }
    }

    bool query(char letter) {
        bool result = false;

        std::vector<TrieNode*> newpaths;
        paths.push_back(root);

        for (TrieNode* it : paths) {
            TrieNode* child = it->children[letter - 'a'];
            if (child) {
                if (child->leaf)
                    result = true;
                newpaths.push_back(child);
            }
        }
        paths = std::move(newpaths);

        return result;
    }

    std::vector<TrieNode*> paths; // keeps track of all possible words that could occur with the next query() call
    TrieNode* root;
};
```

