        parent = parent.fail;
        firstNode = parent.children[letter - 'a'];
      }
      // 2. Reset streamNode to matching node
      streamNode = firstNode != null ? firstNode : root;
      // 3. Check the matching node isWord attribute and find each fail node the true of isWord attribute
      while (firstNode != null && !firstNode.isWord) {
        firstNode = firstNode.fail;
      }
      return firstNode != null && firstNode.isWord;
    }
    private void buildTrie(String[] words) {
      for (String word : words) {
        if (word == null || word.length() <= 0) continue;
        TrieNode current = root;
        for (int i = 0; i < word.length(); i++) {
          char c = word.charAt(i);
          if (current.children[c - 'a'] == null) {
            current.children[c - 'a'] = new TrieNode(c);
          }
          current = current.children[c - 'a'];
        }
        current.isWord = true;
      }
    }
    public void buildFail() {
      // Using BFS to traverse Trie Tree node for building fail pointer
      Queue<TrieNode> queue = new LinkedList<>();
      queue.add(root);
      while (!queue.isEmpty()) {
        TrieNode parent = queue.poll();
        for (TrieNode child : parent.children) {
          if (child != null) {
            TrieNode fail = parent.fail;
            while (fail != null) {
              TrieNode failChild = fail.children[child.val - 'a'];
              if (failChild != null) {
                child.fail = failChild;
                break;
              }
              fail = fail.fail;
            }
            if (child.fail == null) child.fail = root;
            queue.add(child);
          }
        }
      }
    }
    class TrieNode {
      TrieNode(char val) {
        this.val = val;
      }
      char val;      // root val is (char) 0
      TrieNode fail; // root.fail is null
      TrieNode[] children = new TrieNode[26]; // all letters are lower case 
      boolean isWord;
    }
}
