class StreamChecker:

    def __init__(self, words: List[str]):
        # build trie
        trie = {}
        for w in words:
            p = trie
            for c in w:
                if c not in p:
                    p[c] = {'fail':trie}
                p = p[c]
            p['#'] = None

        # rearrange fail pointer, use bfs
        import collections
        d = collections.deque()
        d.append(trie)
        while d:
            cur = d.popleft()
            if cur == trie:
                for c in cur:
                    d.append(cur[c])
            else:
                for c in cur:
                    if c != 'fail' and c != '#':
                        p = cur['fail']
                        while p != trie and c not in p:
                            p = p['fail']
                        if c in p:
                            cur[c]['fail'] = p[c]
                            if '#' in p[c]:
                                cur[c]['#'] = None
                        d.append(cur[c])
        self.trie = trie
        self.p = trie

    def query(self, letter: str) -> bool:
        while True:
            if letter in self.p:
                self.p = self.p[letter]
                break
            if 'fail' in self.p:
                self.p = self.p['fail']
            else:
                break
        return '#' in self.p
