public class StreamChecker
{
    private TrieNode trie = new TrieNode();
    private LinkedList<char> charBuffer = new LinkedList<char>();

    public StreamChecker(string[] words)
    {
        foreach(string w in words)
        {
            TrieNode current = trie;
            for(int i = w.Length - 1; i >= 0; i--)
            {
                char c = w[i];
                if(current.children[c-'a'] is null) current.children[c-'a'] = new TrieNode();
                current = current.children[c-'a'];       
            }
            current.isWord = true;
        }
    }

    public bool Query(char letter)
    {
        charBuffer.AddLast(letter);
        if(charBuffer.Count > 2000) charBuffer.RemoveFirst(); // more than max word length
        LinkedListNode<char> llNode = charBuffer.Last;
        TrieNode trieNode = trie;
        while(llNode != null)
        {
            trieNode = trieNode.children[llNode.Value-'a'];
            if(trieNode is null) return false;
            if(trieNode.isWord) return true;
            llNode = llNode.Previous;
        }
        return false;
    }

    private class TrieNode
    {
        public bool isWord;
        public TrieNode[] children = new TrieNode[26];
    }
}
