class StreamChecker:

    def __init__(self, words: List[str]):
        self.trie = {}
        self.q = deque()
        self.maxqsize = 0
        for word in words: 
            self.maxqsize = max(self.maxqsize, len(word))
            self.construct(word[::-1])

    def construct(self, word):
        trie = self.trie
        for ch in word:
            if ch not in trie:
                trie[ch] = {}
            trie = trie[ch]
        trie['#'] = None

    def findifexist(self, word):
        trie = self.trie
        for ch in word:
            if '#' in trie: return True
            if ch not in trie: break;
            trie = trie[ch] 
        return '#' in trie

    def query(self, letter: str) -> bool:
        self.q.append(letter)
        if len(self.q) > self.maxqsize: self.q.popleft();
        if letter in self.trie:
            tocheck = list(self.q)
            tocheck.reverse()
            return self.findifexist(tocheck)
        return False
