class StreamChecker {
    struct TrieNode {
        bool isEnd;
        TrieNode* children[26];
        TrieNode* suffixLink;
        TrieNode* outputLink;
        TrieNode() {
            isEnd = false;
            memset(children, 0, sizeof(TrieNode*)*26);
            suffixLink = outputLink = NULL;
        }
    };

	TrieNode * root;
	TrieNode * state;

    void buildTrie(vector<string> & words) {
        for (auto & w: words) {
            TrieNode * node = root;
            for (auto c: w) {
                if (!node->children[c-'a'])
                    node->children[c-'a'] = new TrieNode();
                node = node->children[c-'a'];
            }
            node->isEnd = true;
        }
    }

    void buildSuffixLink() {
        queue<TrieNode*> bfsQ;
        root->suffixLink = root;
        bfsQ.push(root);
        while (!bfsQ.empty()) {
            TrieNode * curr = bfsQ.front(); bfsQ.pop();
            for (int i = 0; i < 26; i++) {
                if (curr->children[i]) {
                    TrieNode * suf = curr->suffixLink;
                    while (suf != root && !suf->children[i])
                        suf = suf->suffixLink;
                    if (suf->children[i] && suf != curr)
                        curr->children[i]->suffixLink = suf->children[i];
                    else
                        curr->children[i]->suffixLink = root;
                    bfsQ.push(curr->children[i]);
                }
            }
        }
    }

    void buildOutputLink() {
        queue<TrieNode*> bfsQ;
        root->suffixLink = root;
        bfsQ.push(root);
        while (!bfsQ.empty()) {
            TrieNode * curr = bfsQ.front(); bfsQ.pop();
            TrieNode * suf = curr->suffixLink;
            while (suf != root && !suf->isEnd)
                suf = suf->suffixLink;
            if (suf->isEnd && suf != curr)
                curr->outputLink = suf;
            for (int i = 0; i < 26; i++) {
                if (curr->children[i])
                    bfsQ.push(curr->children[i]);
            }
        }
    }

public:
    StreamChecker(vector<string>& words) {
        root = new TrieNode();
        buildTrie(words);
        buildSuffixLink();
        buildOutputLink();
        state = root;
    }

    bool query(char letter) {
        while (state != root && !state->children[letter-'a'])
            state = state->suffixLink;
        if (state->children[letter-'a'])
            state = state->children[letter-'a'];
        return state->isEnd || state->outputLink;
    }
};
