    if (obj->count >= MAX_SIZE) {
        free(obj->str);
        obj->count = 0;
        obj->str = createString();
    }
    obj->str[obj->count++] = letter;
    int count = obj->count;
    return findWord(obj->root, obj->str, count - 1);
}

