class StreamChecker {
    TrieNode root;
    Queue<TrieNode> q;

    public StreamChecker(String[] words) {
        root = new TrieNode();
        q = new LinkedList<>();
        for (String w : words) {
            build(root, w.toCharArray());
        }
    }

