        q.offer(root);
        int size = q.size();
        boolean res = false;
        int idx = letter - 'a';
        for (int i = 0; i < size; ++i) {
            TrieNode p = q.poll();
            if (p.kids[idx] == null) {
                continue;
            }
            p = p.kids[idx];
            if (p.isWord) {
                res = true;
