class Node:
    def __init__(self):
        self.next = {}
        self.fail = None
        self.is_word = False

class Trie:
    def __init__(self):
        self.root = Node()

    def add(self,word):
        cur = self.root
        for ch in word:
            if ch not in cur.next:
                cur.next[ch] = Node()
            cur = cur.next[ch]

        cur.is_word = True

    def build_failover(self):

        queue = collections.deque()
        for ch in self.root.next:
            self.root.next[ch].fail = self.root
            queue.append(self.root.next[ch])

        while queue:
            node = queue.popleft()
            for ch in node.next:
                queue.append(node.next[ch])
                failover = node.fail
                while True:
                    if failover==None:
                        node.next[ch].fail = self.root
                        break

                    if ch in failover.next:
                        node.next[ch].fail = failover.next[ch]
                        break
                    else:
                        failover = failover.fail

class StreamChecker:

    def __init__(self, words: List[str]):
        self.trie = Trie()
        self.build_trie(words)
        self.cur = self.trie.root

    def build_trie(self,words):
        for word in words:
            self.trie.add(word)

        self.trie.build_failover()

    def query(self, letter: str) -> bool:
        result = False
        while True:
            if letter in self.cur.next:

                self.cur = self.cur.next[letter]

                if self.cur.is_word:
                    result = True

                if self.cur.fail!=None and self.cur.fail.is_word:
                    result = True
                break
            elif self.cur.fail == None:
                self.cur = self.trie.root
                break
            else:
                self.cur = self.cur.fail

        return result

```

