In order to minimize the number of characters that we store in our string, I am using a deque of characters which limits the storage to the maximum length of a string from ```words``` since if present, no suffix will have a length greater than this longest string.

```
class node {
    public:
    node *next[26];
    bool is_end;
    node(bool x=false){
        memset(next,0,sizeof(next));
        is_end=x;
    }
};
class Trie {
public:
    node *root;

    Trie() { root=new node(); }

    void insert(string &word) {
        node *p=root;
        for(int i=0; i<word.length(); i++){
            if(p->next[word[i]-'a']==NULL){
                p->next[word[i]-'a']=new node();
            }
            p=p->next[word[i]-'a'];
        }
        p->is_end=true;
    }

    bool search(deque<char> &dq) {
        node* dummy=root;
        for(auto it=dq.rbegin(); it!=dq.rend(); it++){
            if(!dummy or !dummy->next[*it-'a']){ return false; }
            dummy=dummy->next[*it-'a'];
            if(dummy and dummy->is_end){ return true; }
        }
        return false;
    }
};

class StreamChecker {
public:
    Trie *t=new Trie();
    string s="";
    deque<char> dq;
    int mx=0;
    StreamChecker(vector<string>& words) {
        for(auto &w:words){
            mx=max(mx,(int)w.length());
            reverse(w.begin(),w.end());
            t->insert(w);
        }
    }

    bool query(char letter) {
        dq.push_back(letter);
        if(dq.size()>mx){ dq.pop_front(); }
        return t->search(dq);
    }
};

