class StreamChecker:
    def __init__(self, words: List[str]):
        # build a trie for the words
        self.trie_root = {}
        # a list of valid nodes that can continue from the previous search
        self.candidates = [] 

        # build the trie
        for w in words:
            cur = self.trie_root
            for c in w:
                if c in cur:
                    cur = cur[c]
                else:
                    cur[c] = {}
                    cur = cur[c]
            # mark end of word
            cur["#"] = {}   

    def query(self, letter: str) -> bool:
        new_candidates = []
        found_match = False
        # a new candidate
        if letter in self.trie_root:
            node = self.trie_root[letter]
            if "#" in node:
                found_match = True
                # it's possible that the matched word is a prefix 
                # of another word
                if len(node) > 1:
                    new_candidates.append(node)
            else:
                new_candidates.append(node)

        # iterate over candidates to check if the candidates are still valid
        # or if it matches a word (can be both)
        for node in self.candidates:
            if letter in node:
                next_node = node[letter]
                if "#" in next_node:
                    found_match = True
                    if len(next_node) > 1:
                        new_candidates.append(next_node)
                else:
                    new_candidates.append(next_node)
        self.candidates = new_candidates

        return found_match


