class StreamChecker {
public:

    struct TrieNode
    {
        TrieNode * child[26];
        bool word;
    };

    TrieNode * getNode()
    {
        TrieNode * node = new TrieNode();
        for(int i=0;i<26;i++)node->child[i]=NULL;
        node->word = false;

        return node;
    }

    // add words in reverse order in trie
    void addWord(TrieNode *root, string &str)
    {
        int sl = str.length();
        TrieNode * node = root;
        int index;
        for(int i=sl-1;i>=0;i--)
        {
            index = str[i]-'a';
            if(node->child[index]==NULL)
                node->child[index]=getNode();

            node = node->child[index];
        }

        node->word = true;
    }

    // search query string in reverse order in trie up to max length words or query string
    bool searchWord(TrieNode *root, string &str)
    {
        int sl = str.length();
        TrieNode * node = root;
        int index;
        for(int i=sl-1,j=0;i>=0 && j<maxWordLength;i--,j++)
        {
            index = str[i]-'a';
            if(node->child[index]==NULL)return false;

            node = node->child[index];
            if(node->word)return true;
        }

        return false;
    }

    TrieNode *root;
    string qString;
    int maxWordLength = INT_MIN;

    StreamChecker(vector<string>& words) {

        // build up trie with given words
        root = getNode();
        for(string &word : words)
        {
            addWord(root,word);
            maxWordLength = max(maxWordLength,(int)word.length());
        }
    }

    bool query(char letter) {

        // append letter in query string and search
        qString.push_back(letter);
        return searchWord(root,qString);
    }
};
