Solution with entering every word in Reverse order in the Trie and then checking them if isWord or not.

class StreamChecker {

    class TrieNode
    {
        Map<Character,TrieNode> child;
        boolean isWord;

        TrieNode()
        {
            child=new HashMap<>();
            isWord=false;
        }
    }

    TrieNode root;
    StringBuilder sb;

    public StreamChecker(String[] words) {
        root=new TrieNode();
        sb=new StringBuilder("");

        TrieNode current=root;

        for(String s:words)
        {
            add(current,s,s.length()-1);
        }
    }

    public void add(TrieNode current,String s,int index)
    {
           if(index<0)
           {
               current.isWord=true;
               return;
           }

           if(current.child.containsKey(s.charAt(index)))
           {
               current=current.child.get(s.charAt(index));
               add(current,s,index-1);
           }else
           {
             TrieNode node=new TrieNode();
             current.child.put(s.charAt(index),node);
             current=current.child.get(s.charAt(index));
             add(current,s,index-1);
           }
    }

    public boolean isWord(TrieNode current,StringBuilder s, int i)
    {
        if(i<0)
        {
            return false;
        }

        char ch=sb.charAt(i);
        if(current.child.containsKey(ch))
        {
            TrieNode n=current.child.get(ch);
            if(n.isWord)
            {
                return true;
            }else
            {
                current=current.child.get(ch);
                return isWord(current,s,i-1);
            }
        }

        return false;
    }

    public boolean query(char letter) {
        TrieNode current=root;
        sb.append(letter);

        boolean found=isWord(current,sb,sb.length()-1);

        return found;
    }
}

