class StreamChecker {
public:
    StreamChecker(vector<string>& words) {
        for (const string& word : words) {
            TrieNode *node = root;
            for (int i = word.size() - 1; i >= 0; --i) { //Reversed storing info in trie
                if (!node->next[word[i] - 'a']) {
                    node->next[word[i] - 'a'] = new TrieNode();
                }
                node = node->next[word[i] - 'a'];
            }
            node->isWord = true;
        }
    }

    bool query(char letter) {
        queryStr+=letter;        
        TrieNode *node = root;
        for (int i = queryStr.size() - 1; i >= 0 && node; --i) { //Reversed searching
            node = node->next[queryStr[i] - 'a'];
            if (node && node->isWord) return true;
        }
        return false;
    }

private:
    struct TrieNode {
        bool isWord;
        TrieNode *next[26];
    };

    TrieNode *root = new TrieNode();
    string queryStr;
};
