class Vertex:
    def __init__(self, p = -1, pch = '#'):
        self.next = {}
        self.leaf = False
        self.p = p
        self.pch = pch
        self.link = -1
        self.go = {}


class Bor:
    def __init__(self):
        self.t = [Vertex()]
        self.memo_leaf = {}

    def add_string(self, s):
        v = 0
        for ch in s:
            if ch not in self.t[v].next:
                self.t[v].next[ch] = len(self.t)
                self.t.append(Vertex(v, ch))
            v = self.t[v].next[ch]
        self.t[v].leaf = True

    def go(self, v, c):
        if c not in self.t[v].go:
            if c in self.t[v].next:
                self.t[v].go[c] = self.t[v].next[c]
            else:
                self.t[v].go[c] = self.go(self.get_link(v), c) if v != 0 else 0
        return self.t[v].go[c]

    def get_link(self, v):
        if self.t[v].link == -1:
            if v == 0 or self.t[v].p == 0:
                self.t[v].link = 0
            else:
                self.t[v].link = self.go(self.get_link(self.t[v].p), self.t[v].pch)
        return self.t[v].link

    def is_leaf(self, v):
        if v not in self.memo_leaf:
            self.memo_leaf[v] = v != 0 and (self.t[v].leaf or self.is_leaf(self.get_link(v)))
        return self.memo_leaf[v]


class StreamChecker:
    def __init__(self, words):
        self.bor = Bor()
        for w in words:
            self.bor.add_string(w)
        self.v = 0

    def query(self, letter):
        self.v = self.bor.go(self.v, letter)
        return self.bor.is_leaf(self.v)
