This problem is a typical application of aho corasick algorithm. Here, I have implemented Aho-corasick with the help of trie. 
```
class TrieNode{
        public:
  TrieNode *adj[26];
  TrieNode *failure;
    bool isEnd;

    TrieNode() {
        for(int i = 0;i<26;i++){
            adj[i]=NULL;
            failure = NULL;
            isEnd=false;
        }
    }
};
void insert(TrieNode *root, string s){
    TrieNode *tmp = root;
    for(char c : s) {
        if(tmp->adj[c-'a']==NULL){
            tmp->adj[c-'a'] = new TrieNode();
        }
        tmp = tmp->adj[c-'a'];
    }
    tmp->isEnd = true;
}

void build(TrieNode *root) {
    root->failure = root;
    queue<TrieNode*> q;
    for(int i = 0;i<26;i++){
        if(root->adj[i]!=NULL){
            root->adj[i]->failure = root;
            q.push(root->adj[i]);
        }else{
            root->adj[i] = root;
        }
    }
    while(!q.empty()) {
        TrieNode* u = q.front();
        q.pop();
        for(int i = 0;i<26;i++) {
            if(u->adj[i]!=NULL) {
                TrieNode *failure = u->failure;
                while(failure->adj[i]==NULL){
                    failure = failure->failure;
                }
                failure = failure->adj[i];
                u->adj[i]->failure = failure;
                u->adj[i]->isEnd = u->adj[i]->isEnd || failure->isEnd;
                q.push(u->adj[i]);
            }
        }
    }
}
class StreamChecker {
public:
    TrieNode *root;
    TrieNode *curr;
    StreamChecker(vector<string>& words) {
        root = new TrieNode();
        for(string s:words){
            insert(root,s);
        }
        build(root);
        curr = root;
    }

    bool query(char letter) {

        while(curr->adj[letter-'a']==NULL)
            curr = curr->failure;
        curr = curr->adj[letter-'a'];
        return curr->isEnd;
    }
};

