class StreamChecker {
    TrieNode root;
    ArrayList<TrieNode> waiting;
    public StreamChecker(String[] words) {
        root = new TrieNode();
        root.word = "/";
        for(String i:words){
            insert(root, i);
        }
        waiting = new ArrayList<TrieNode>();
    }

    public boolean query(char letter) {
        TrieNode rootNext = root.links[letter-'a'], temp = null;
        boolean ans = false;
        int loop = waiting.size();
        for(int i=0;i<loop;i++){
            temp = waiting.get(i).links[letter-'a'];
            if(temp==null){
                waiting.remove(i);
                i-=1;
                loop-=1;                
            }
            else{
                if(temp.word!=null){
                    ans = true;
                }                
                waiting.set(i, temp);
            }
        }
        if(rootNext!=null){
            waiting.add(rootNext);
            if(rootNext.word!=null){
                ans = true;
            }
        }

        return ans;
    }
    public void insert(TrieNode root, String word){
        int n = word.length();
        TrieNode temp = root;
        for(char i:word.toCharArray()){
            if(temp.links[i-'a']==null){
                temp.links[i-'a'] = new TrieNode();                
            }
            temp = temp.links[i-'a'];
        }
        temp.word = word;
    }
}
class TrieNode{
    String word;
    TrieNode[] links;
    TrieNode(){        
        links = new TrieNode[26];
    }
}
