		q = new ArrayDeque<>();

		for (String word : words) {
			root.insert(word);
		}

		q.add(root);
	}

	public boolean query(char letter) {
		boolean flag = false;

		int size = q.size();

		for (int i = 0; i < size; i++) {
			TrieNode n = q.poll();

			if (n.children.containsKey(letter)) {
				q.add(n.children.get(letter));
				if (n.children.get(letter).isLeaf)
					flag = true;
			}

			if (n == root)
				q.add(root);

		}

		return flag;

	}
