Idea: Use suffix tree instead of prefix tree to allow efficient histroy search, since that works in a backward way.

```c++
class TrieNode {
public:
    TrieNode() : word_end(false) {
        children = vector<TrieNode*>(alphabet_size);
    }

    ~TrieNode() {
        for (auto child : children) {
            if (child)
                delete child;
        }
    }

    TrieNode* get(char ch) {
        int idx = ch - 'a';
        return children[idx];
    }

    void set(char ch) {
        int idx = ch - 'a';
        children[idx] = new TrieNode();
    }

    bool isEnd() {
        return word_end;
    }

    void setEnd() {
        word_end = true;
    }

private:
    static constexpr int alphabet_size = 26;
    bool word_end;
    vector<TrieNode*> children;
};

class Trie {
public:
    Trie() {
        root = new TrieNode();
    }

    ~Trie() {
        delete root;
    }

    void reverseInsert(const string& word) {
        TrieNode* node = root;
        for (int i = word.size() - 1; i >= 0; i--) {
            char ch = word[i];
            if (!node->get(ch)) {
                node->set(ch);
            }
            node = node->get(ch);
        }
        node->setEnd();
    }

    bool historySearch(deque<char>& history) {
        TrieNode* node = root;
        for (int i = history.size() - 1; i >= 0; i--) {
            node = node->get(history[i]);
            if (!node)
                break;
            if (node->isEnd())
                return true;
        }
        return false;
    }

private:
    TrieNode* root;
};

class StreamChecker {
public:
    StreamChecker(vector<string>& words) {
        for (string &word : words)
            trie.reverseInsert(word);
    }

    bool query(char letter) {
        history.push_back(letter);
        if (history.size() > wordLenLimit)
            history.pop_front();

        return trie.historySearch(history);
    }

private:
    Trie trie;
    const int wordLenLimit = 2000;
    deque<char> history;
};

