class StreamChecker {
public:

    StreamChecker(const vector<string>& words) {
        for (const string& w : words) words_.emplace(w.rbegin(), w.rend());
    }

    bool query(char letter) {
        stream_ += letter;
        string pref;
        for (int k=0; k < stream_.size(); k++) {
            pref += stream_[stream_.size() - 1 - k];

            auto begin = words_.lower_bound(pref);
            pref.back()++;
            auto end = words_.lower_bound(pref);
            pref.back()--;

            if (begin == end) return false;
            if (*begin == pref) return true;
        }
        return false;
    }

    string stream_;
    set<string> words_;
};

