class StreamChecker {
    private class Trie {
        Map<Character, Trie> map = new HashMap<>();
        boolean isLast;
    }

    private final Trie trie;
    private final FixedRotationList history;

    public StreamChecker(String[] words) {
        this.trie = new Trie();
        this.history = new FixedRotationList();


        for (String word : words) {
            Trie cur = this.trie;

            char[] arr = word.toCharArray();

            /**
            * we use a reverse trie so we fill it in reverse order
            */
            for (int i = arr.length-1; i >= 0; i--) { // insert into the tree
                if (!cur.map.containsKey(arr[i])) {
                    cur.map.put(arr[i], new Trie());
                } 

                cur = cur.map.get(arr[i]);            
            }

            cur.isLast = true;
        }
    }



    public boolean query(char letter) {
        history.add(letter);

        Trie cur = this.trie; // get a link in order update it futher

        /**
        * insofar we use the reverse trie, so we iterate in reverse order
        */
        for (int i = history.size()-1; i >= 0; i--) {
            Character curLetter = history.get(i); 

            if (cur == null || !cur.map.containsKey(curLetter)) return false;

            if (cur.map.containsKey(curLetter) && cur.map.get(curLetter).isLast) return true;

            cur = cur.map.get(curLetter);
        }

        return false;
    }
}

class FixedRotationList {
    private final int MAX_LENGTH = 2_000;
    private List<Character> list = new ArrayList<>(MAX_LENGTH);
    private int curLen = 0;

    public int size() {
        return list.size();
    }

    public Character get(int idx) {
        if (list.isEmpty())
            return null;

        if (list.size() < MAX_LENGTH)
            return list.get(idx);


        idx = (idx + curLen) % MAX_LENGTH;

        return list.get(idx);
    }

    public void add(char letter) {
        if (curLen >= MAX_LENGTH) {
            list.set(curLen%MAX_LENGTH, letter);
        } else {
            list.add(letter);
        }


        curLen++;
    }
}
