class Trie {
private:
    vector<vector<int>> trie;
    vector<bool> isWord;
    vector<int> fail;
    int curPos;
public:
    Trie() {
        trie.resize(1, vector<int>(28));
        isWord.resize(1);
        fail.resize(1, -1);
        curPos = 0;
    }

    int alloc(char ch) {
        trie.push_back(vector<int>(28));
        trie[trie.size()-1][27] = ch-'a';
        isWord.push_back(0);
        fail.push_back(0);
        return trie.size()-1;
    }
    void buildFail() {
        queue<pair<int, int>> q;
        q.push(make_pair(0, -1));
        while(!q.empty()) {
            auto curNode = q.front(); q.pop();
            int cur = curNode.first;
            int fa = curNode.second;
            if(cur > 0) {
                int chIndex = trie[cur][27];
                int failFa = fail[fa];
                while(failFa != -1) {
                    if(!trie[failFa][chIndex]) {
                        failFa = fail[failFa];
                    } else {
                        break;
                    }
                }
                if(failFa == -1) {
                    fail[cur] = 0;
                } else {
                    fail[cur] = trie[failFa][chIndex];
                }
            }
            for(int i=0; i<27; i++) {
                if(!trie[cur][i]) continue;
                q.push(make_pair(trie[cur][i], cur));
            }
        }


        return;
    }
    void insert(string& word) {
        int cur = 0;
        for(char ch : word) {
            int index = ch-'a';
            if(!trie[cur][index]) {
                trie[cur][index] = alloc(ch);
            }
            cur = trie[cur][index];
        }
        isWord[cur] = 1;
        return;
    }
    bool find(char ch) {
        int index = ch-'a';
        int failFa = curPos;
        while(failFa != -1) {
            if(trie[failFa][index]) {
                curPos = trie[failFa][index];
                int tmp =curPos;
                while(tmp != -1) {
                    if(isWord[tmp]) return 1;
                    tmp = fail[tmp];
                }
                return 0;
            }
            failFa = fail[failFa];
        }
        curPos = 0;
        return 0;
    }
};
class StreamChecker {
private:
    Trie trie;
public:
    StreamChecker(vector<string>& words) {
        for(auto& str : words) {
            trie.insert(str);
        }
        trie.buildFail();
    }

    bool query(char letter) {
        return trie.find(letter);
    }
};

